@Test
void nullPathString() {
    assertThatIllegalArgumentException().isThrownBy(() -> new PathResource((String) null)).withMessageContaining("Path must not be null");
}

@Test
void nullUri() {
    assertThatIllegalArgumentException().isThrownBy(() -> new PathResource((URI) null)).withMessageContaining("URI must not be null");
}

@Test
void createFromPath() {
    Path path = Paths.get(TEST_FILE);
    PathResource resource = new PathResource(path);
    assertThat(resource.getPath()).isEqualTo(TEST_FILE);
}

@Test
void createFromString() {
    PathResource resource = new PathResource(TEST_FILE);
    assertThat(resource.getPath()).isEqualTo(TEST_FILE);
}

@Test
void createFromUri() {
    File file = new File(TEST_FILE);
    PathResource resource = new PathResource(file.toURI());
    assertThat(resource.getPath()).isEqualTo(file.getAbsoluteFile().toString());
}

@Test
void getPathForFile() {
    PathResource resource = new PathResource(TEST_FILE);
    assertThat(resource.getPath()).isEqualTo(TEST_FILE);
}

@Test
void getPathForDir() {
    PathResource resource = new PathResource(TEST_DIR);
    assertThat(resource.getPath()).isEqualTo(TEST_DIR);
}
