                    break;
                case Opcodes.T_CHAR:
                    push(ARRAY_OF | CHAR);
                    break;
                case Opcodes.T_BYTE:
                    push(ARRAY_OF | BYTE);
                    break;
                case Opcodes.T_SHORT:
                    push(ARRAY_OF | SHORT);
                    break;
                case Opcodes.T_INT:
                    push(ARRAY_OF | INTEGER);
                    break;
                case Opcodes.T_FLOAT:
                    push(ARRAY_OF | FLOAT);
                    break;
                case Opcodes.T_DOUBLE:
                    push(ARRAY_OF | DOUBLE);
                    break;
                case Opcodes.T_LONG:
                    push(ARRAY_OF | LONG);
                    break;
                default:
                    throw new IllegalArgumentException();
            }
            break;
