    {
      "head": "org.springframework.core.env.ProfilesParser#parseTokens(String,StringTokenizer,Context)",
      "head_type": "method",
      "relation": "have",
      "tail": "private static Profiles parseTokens(String expression, StringTokenizer tokens, Context context) {
    List<Profiles> elements = new ArrayList<>();
    Operator operator = null;
    while (tokens.hasMoreTokens()) {
        String token = tokens.nextToken().trim();
        if (token.isEmpty()) {
            continue;
        }
        switch(token) {
            case "(" ->
                {
                    Profiles contents = parseTokens(expression, tokens, Context.PARENTHESIS);
                    if (context == Context.NEGATE) {
                        return contents;
                    }
                    elements.add(contents);
                }
            case "&" ->
                {
                    assertWellFormed(expression, operator == null || operator == Operator.AND);
                    operator = Operator.AND;
                }
            case "|" ->
                {
                    assertWellFormed(expression, operator == null || operator == Operator.OR);
                    operator = Operator.OR;
                }
            case "!" ->
                elements.add(not(parseTokens(expression, tokens, Context.NEGATE)));
            case ")" ->
                {
                    Profiles merged = merge(expression, elements, operator);
                    if (context == Context.PARENTHESIS) {
                        return merged;
                    }
                    elements.clear();
                    elements.add(merged);
                    operator = null;
                }
            default ->
                {
                    Profiles value = equals(token);
                    if (context == Context.NEGATE) {
                        return value;
                    }
                    elements.add(value);
                }
        }
    }
    return merge(expression, elements, operator);
}",
