}

package org.springframework.core.metrics.jfr;
import java.util.function.Consumer;
public class FlightRecorderStartupStep {
    private Event event;
    private Consumer<FlightRecorderStartupStep> recordingCallback;
    private Map<String, String> tags;
    @Override
    public void end() {
        this.event.end();
        if (this.event.shouldCommit()) {
            StringBuilder builder = new StringBuilder();
            this.tags.forEach(tag -> builder.append(tag.getKey()).append('=').append(tag.getValue()).append(','));
            this.event.setTags(builder.toString());
        }
        this.event.commit();
        this.recordingCallback.accept(this);
    }
}

